mapper.select:
		select * from
		(select t1.*,rownum rn from
		(select my_emp.*,my_dept.dname,my_dept.loc 
  			from my_emp join my_dept on my_emp.deptNo=my_dept.deptno 
  			order by empNo
  		)t1


  		and job=#{job}


  		and dName=#{dName}


  		and loc=#{loc}


  		) where rn>=(#{bean.currNum}-1)*#{bean.pageSize}+1 and rn<=#{bean.currNum}*#{bean.pageSize}

		select count(1) from
			(select my_emp.*,my_dept.dname,my_dept.loc,rownum rn 
 			from my_emp join my_dept on my_emp.deptNo=my_dept.deptno 
  			order by empNo
  			)


  			and job=#{job}


  			and dName=#{dName}


  			and loc=#{loc}
